import React, { Component } from 'react';
import axios from 'axios';


const myStryle = {
    color: "black",
    marginLeft:"20px",
}

const divStyle = {
    backgroundColor:"lightblue" ,
    marginTop :"10px", 
    borderRadius:"10px"
}


const buttonStyle ={
    marginLeft:"20px",
    marginBottom:"10px",
    borderRadius:"10px"
}

class NewElection extends Component {

    constructor(props) {
        super(props);
        this.state = {
            election_name: '',
            election_organizer: '',
            election_password: '',
        };
    }

    handleInputChange = e => {
        this.setState({
        [e.target.name]: e.target.value,
        });
    };

    handleSubmit = (e) => {
        e.preventDefault();
        const { election_name, election_organizer, election_password } = this.state;
        console.log(election_name);
        axios.post('http://localhost:8000/api/electionName', {
            election_name: election_name,
            election_organizer: election_organizer,
            election_password: election_password
        })
        .then(function(response){ 
            window.location.assign('/adminPannel');
        })
        .catch(function(err){
            console.error(err);
        });
    }

    render(){
        return(
            <div className="container" style ={divStyle}>
                <h4>Create New Election</h4>
                    <form onSubmit={this.handleSubmit}>
                        <label htmlFor="name" style={myStryle}>Election Name</label>
                        <input type="text" id="election_name" name="election_name" onChange={this.handleInputChange} required/>
                        <br></br>
                        <label htmlFor="name" style={myStryle}>Election Organizer</label>
                        <input type="text" id="election_organizer" name="election_organizer" onChange={this.handleInputChange} required/>
                        <br></br>
                        <label htmlFor="name" style={myStryle}>Election Password</label>
                        <input type="password" id="election_password" name="election_password" onChange={this.handleInputChange} required/>
                        <br></br><br></br>
                        <button className="btn blue darken-2" type="submit" name="action" style={buttonStyle}>Submit
                            <i className="material-icons right">send</i>
                        </button>
                    </form>
            </div>
        )
    }
}

export default NewElection;
