import React, { Component } from 'react'
import axios from 'axios'


const myStryle = {
    color: "black",
    marginLeft:"20px",

}

const buttonStyle ={
    marginLeft:"20px",
    marginBottom:"10px",
    borderRadius:"10px"
}

const divStyle = {
    backgroundColor:"lightblue" ,
    marginTop :"10px", 
    borderRadius:"10px"
}

class Login extends Component {

    constructor(props){
        super(props)
        this.state = {
            'username': null,
            'password': null
        }
    }

    handleInputChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value,
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        const { username, password } = this.state;
        console.log(username);
        console.log(password);
        // axios.post('http://localhost:8000/api/adminLogin', {
        //     username: username,
        //     password: password,
        // })
        // .then(function(response){ 
        //     if(response.data){
        //         window.location.assign("/newelection")
        //     }else{
        //         alert('Incorrect Username or Password');
        //     }
        // })
        // .catch(function(err){
        //     console.error(err);
        // });
        if(username =="Lakshit" && password =="Lakshit"){
            window.location.assign("/adminPannel");
        }

        else{
            alert('Incorrect username and password');
        }
    }



    render(){
        return(
            <div className="container" style ={divStyle}>
                <form  onSubmit={this.handleSubmit}>
                    <div >
                        <input type="text" id="username" name="username" onChange={this.handleInputChange} required/>
                        <label htmlFor="name" style={myStryle}>Username</label><br></br>
                    </div>
                    <div>
                        <input type="password" id="password" name="password" onChange={this.handleInputChange} required/>
                        <label htmlFor="name"style={myStryle}>Password</label><br></br><br></br>
                    </div>
                    <button className="btn blue darken-2 center" style={buttonStyle} type="submit" name="action">Submit
                        <i className="material-icons right">send</i>
                    </button>
                </form>
            </div>      
        )
    }
}

export default Login;
