import React, { Component } from 'react';
import { Link } from 'react-router-dom';


// const divStyle = {
//     backgroundColor:"lightblue" ,
//     marginTop :"10px", 
//     borderRadius:"10px"
// }

class Home extends Component {

    constructor(props){
        super(props);
        this.state = {
            showComponents: false
        }
        this.state = {value: 'Choose'};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event) 
    {
      this.setState({value: event.target.value});
    }
  handleSubmit(event)
    {
          // event.preventDefault()
          if(this.state.value=='Choose')
          {
          this.props.history.push('/Choose');
          event.preventDefault();
          }
          else{
               this.props.history.push('/Login');
               event.preventDefault();
              }
    }



    render(){
        return (
            <div role="combobox" aria-haspopup="listbox" aria-owns="react-autowhatever-1" aria-expanded="false" style="list-style-type: none; display: block; width: 100%; position: absolute; background-color: rgb(255, 255, 255); font-family: Helvetica, sans-serif; font-weight: 300; font-size: 16px; border-radius: 4px; z-index: 2; padding-left: 0px; padding-right: 0px; border: 1px solid rgb(204, 204, 204);">
             <form onSubmit={this.handleSubmit}>
              <div class="form-group" >
                <label style={{color: "rgba(255, 217, 0, 0.87)"}}>
                    Choose Below, The type of User         :
                    <hr></hr>
                <select  class="form-control" value={this.state.value} onChange={this.handleChange}>
                <option value="Choose">Voter</option>
                <option value="Login">Admin</option>
                </select>
                </label>
                <br />
                <br />
                <br />
                <button type="submit" class="btn btn-primary" onClick={this.props.onSubmit} >Submit</button>
              </div>
             </form>
            </div>

            // <div className="container">
            //     <ul className="collection with-header">
            //         <li className="collection-header" style={divStyle}><h3 style={{color: "red"}}>Choose User Type</h3></li>
            //         <li className="collection-item" style={divStyle}><div><h4 >User<Link to="/choose" className="secondary-content"><i className="material-icons">send</i></Link></h4></div></li>
            //         <li className="collection-item" style={divStyle}><div><h4 >Admin<Link to="/login" className="secondary-content"><i className="material-icons">send</i></Link></h4></div></li>
            //     </ul>
            // </div>
        )
    }
}

export default Home
